  `materials` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT '스타일에 맞는 소재/마감 배열',
  `scent_music` text DEFAULT NULL COMMENT '스타일 분위기를 표현하는 향/음악 키워드',
